Here's a simple example of a crowdsale smart contract in Solidity:

```solidity
pragma solidity ^0.8.0;

contract SimpleCrowdsale {
    address public owner;
    uint256 public rate = 1000; // Number of tokens per Ether
    ERC20Token public token;

    constructor(address _tokenAddress) {
        owner = msg.sender;
        token = ERC20Token(_tokenAddress);
    }

    receive() external payable {
        buyTokens();
    }

    function buyTokens() public payable {
        uint256 tokenAmount = msg.value * rate;
        require(token.balanceOf(address(this)) >= tokenAmount, "Not enough tokens available");
        token.transfer(msg.sender, tokenAmount);
    }

    function withdrawFunds() public {
        require(msg.sender == owner, "Only owner can withdraw funds");
        payable(owner).transfer(address(this).balance);
    }

    function endCrowdsale() public {
        require(msg.sender == owner, "Only owner can end crowdsale");
        uint256 remainingTokens = token.balanceOf(address(this));
        if (remainingTokens > 0) {
            token.transfer(owner, remainingTokens);
        }
        selfdestruct(payable(owner));
    }
}

interface ERC20Token {
    function transfer(address recipient, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}
```

This contract allows users to buy tokens by sending Ether to the contract. It includes basic functions for buying tokens, withdrawing funds by the owner, and ending the crowdsale.
